import { useState } from 'react';

function Templates() {
  const [activeCategory, setActiveCategory] = useState('全部');
  
  const categories = ['全部', '商务', '旅行', '娱乐', '教育', '社交'];

  return (
    <div className="min-h-screen bg-[#1a1c2e] text-white pb-16">
      {/* 顶部导航 */}
      <div className="px-4 py-3 flex items-center justify-between">
        <h1 className="text-xl font-medium bg-gradient-to-r from-[#ff6b6b] to-[#4ecdc4] bg-clip-text text-transparent">
          模板中心
        </h1>
        <button className="w-8 h-8 flex items-center justify-center bg-white/10 rounded-full">
          <i className="fas fa-search text-white/80"></i>
        </button>
      </div>

      {/* 分类标签 */}
      <div className="px-4 overflow-x-auto">
        <div className="flex space-x-3 py-4">
          {categories.map(category => (
            <button
              key={category}
              className={`px-4 py-1.5 rounded-full text-sm whitespace-nowrap ${
                activeCategory === category 
                  ? 'bg-blue-500 text-white' 
                  : 'bg-white/10 text-white/80'
              }`}
              onClick={() => setActiveCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* 模板列表 */}
      <div className="px-4 grid grid-cols-2 gap-4">
        {[1,2,3,4].map(item => (
          <div key={item} className="bg-[#1e2642] rounded-xl overflow-hidden">
            <div className="aspect-video bg-black/30 relative">
              <img 
                src={`https://picsum.photos/300/200?random=${item}`} 
                alt="模板预览"
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0 flex items-center justify-center">
                <i className="fas fa-play-circle text-white/80 text-3xl"></i>
              </div>
            </div>
            <div className="p-3">
              <h3 className="text-sm font-medium">商务模板 {item}</h3>
              <div className="flex items-center justify-between mt-2 text-xs text-gray-400">
                <span>使用 1.2k</span>
                <div className="flex items-center space-x-2">
                  <i className="fas fa-heart"></i>
                  <span>268</span>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Templates;
